import React, {useState} from 'react';
import Routes from './routes';
import Context from './utils/context'



const App = () => {

  const [stateGlobal, setStateGlobal] = useState(0)

  const incrementGlobal = () => {
    setStateGlobal(stateGlobal + 1)
  }

  const decrementGlobal = () => {
    setStateGlobal(stateGlobal - 1)
  }
    return(
      <div>
      React
        <Context.Provider
          value={{
            valueGlobalState: stateGlobal,
            addGlobalValue: () => incrementGlobal(),
            decGlobalValue: () => decrementGlobal(),
          }}
        />
        <Routes />
       <Context.Provider/>
      </div>
    )
}


export default App;
